import React, { useState, useRef, useCallback, ChangeEvent, useEffect } from 'react';
import ExcelJS from 'exceljs';
import { saveAs } from 'file-saver';

import {
  ShoppingCart,
  Upload,
  Edit3,
  Save,
  Trash2,
  Eye,
  Plus,
  FileText,
  Calendar,
  User,
  Phone,
  Mail,
  Package, ChevronLeft, ChevronRight 
} from 'lucide-react';

import Papa, { ParseResult } from 'papaparse'
import productsCsv from '././db/Products.csv';

interface products {
  id: number;
  name: string;
  basePrice: number;
  image: string;
  type: string;
}


interface OrderItem {
  id: number;
  product: products;
  color: string;
  size: string;
  quantity: number;
  logo: string | null;
  logoPosition: { x: number; y: number };
  subtotal: number;
}

interface CustomerInfo {
  companyName: string;
  contact: string;
  phone: string;
  email: string;
  deliveryDate: string;
}

interface Order {
  id: number;
  customer: CustomerInfo;
  items: OrderItem[];
  total: number;
  status: string;
  createdAt: string;
  deliveryDate: string;
}

// Función para importar todas las imágenes del directorio imgs
const importAll = (r: __WebpackModuleApi.RequireContext) => {
  let images: { [key: string]: string } = {};
  r.keys().forEach((item: string) => {
    images[item.replace('./', '')] = r(item);
  });
  return images;
};
const images = importAll(require.context('./imgs', false, /\.(png|jpe?g|svg)$/));


const OrderManagementSystem = () => {
  const [orders, setOrders] = useState({
    pending: 2,
    inProcess: 1,
    completed: 5,
    todaySales: 30
  });

  const [products, setProducts] = useState<products[]>([]);
  const [selectedProduct, setSelectedProduct] = useState<products | null>(null);
  const [selectedColor, setSelectedColor] = useState('#3B82F6');
  const [logoFile, setLogoFile] = useState<string | null>(null);
  const [logoPosition, setLogoPosition] = useState({ x: 0, y: 0 });
  const [orderItems, setOrderItems] = useState<OrderItem[]>([]);
  const [quantity, setQuantity] = useState(1);
  const [selectedSize, setSelectedSize] = useState('M');
  const [nextOrderItemId, setNextOrderItemId] = useState(1);

  const [currentView, setCurrentView] = useState('new-order');
  const [currentProductIndex, setCurrentProductIndex] = useState(0);

  const [customerInfo, setCustomerInfo] = useState<CustomerInfo>({
    companyName: 'Empresa ABC',
    contact: 'Juan Pérez',
    phone: '555-1234',
    email: 'cliente@empresa.com',
    deliveryDate: ''
  });

  const fileInputRef = useRef<HTMLInputElement>(null);

  const colors = [
    '#3B82F6', '#000000', '#A16207', '#DC2626', '#D97706', '#EAB308',
    '#16A34A', '#059669', '#0891B2', '#2563EB', '#7C3AED', '#FFFFFF',
    '#6B7280', '#F3F4F6', '#FCA5A5', '#FEF3C7', '#BBF7D0', '#BFDBFE',
    '#9CA3AF', '#EF4444'
  ];

useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch(productsCsv);
        const reader = response.body?.getReader();
        const result = await reader?.read();
        const decoder = new TextDecoder('utf-8');
        const csv = decoder.decode(result?.value);

        Papa.parse(csv, {
          header: true,
          complete: (result: ParseResult<any>) => {
            const parsedProducts = result.data.map((item: any, index: number) => {
              const fileName = item.Link.split('\\').pop();
              return {
                id: index + 1,
                name: item.Producto,
                basePrice: parseInt(item.Costo),
                image: images[fileName] || '',
                type: item.Tipo,
              };
            });
            setProducts(parsedProducts);
          },
          skipEmptyLines: true,
        });
      } catch (error) {
        console.error('Error fetching or parsing CSV:', error);
      }
    };

    fetchProducts();
  }, []); // El array vacío asegura que se ejecute solo una vez al montar el componente


const sizes = ['Chico', 'Mediano', 'Grande', 'Extra Grande'];

const handleLogoUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
  const file = event.target.files?.[0];
  if (file) {
    const reader = new FileReader();
    reader.onload = () => {
      setLogoFile(reader.result as string);
    };
    reader.readAsDataURL(file);
  }
};


const handleLogoPosition = useCallback((event: React.MouseEvent<HTMLDivElement>) => {
  // Asegúrate de que un logo haya sido subido antes de intentar posicionarlo
  if (!logoFile) return;

  // Obtiene el div que maneja el evento de clic
  const previewDiv = event.currentTarget;

  // Obtiene las coordenadas del div de la prenda en la ventana
  const rect = previewDiv.getBoundingClientRect();

  // Calcula la posición del clic en relación con el div de la prenda
  const x = event.clientX - rect.left;
  const y = event.clientY - rect.top;

  // Actualiza el estado del logo con las nuevas coordenadas
  setLogoPosition({ x, y });
}, [logoFile]); // El Hook useCallback se re-ejecuta si el logoFile cambia


  const addProductToOrder = () => {
    if (!selectedProduct) {
      alert('Por favor selecciona un producto');
      return;
    }

    const newItem = {
      id: Date.now(),
      product: selectedProduct,
      color: selectedColor,
      size: selectedSize,
      quantity: Number(quantity),
      logo: logoFile,
      logoPosition,
      subtotal: selectedProduct.basePrice * Number(quantity),
    };

    setOrderItems([...orderItems, newItem]);

    // Reset form
    setSelectedProduct(null);
    setSelectedColor('#3B82F6');
    setQuantity(5);
    setLogoFile(null);
    setLogoPosition({ x: 50, y: 50 });
  };

const removeOrderItem = (id: number) => {
    setOrderItems(orderItems.filter(item => item.id !== id));
  };

  const updateOrderItem = (id: number, field: keyof OrderItem, value: any) => {
    setOrderItems(orderItems.map(item => 
      item.id === id 
        ? { ...item, [field]: value, subtotal: field === 'quantity' ? item.product.basePrice * value : item.subtotal }
        : item
    ));
  };

const getTotalOrder = (): number => {
  return orderItems.reduce((sum, item) => sum + item.subtotal, 0);
};

  const saveOrder = () => {
    if (orderItems.length === 0) {
      alert('Agrega al menos un producto a la orden');
      return;
    }

    if (!customerInfo.deliveryDate) {
      alert('Por favor ingresa la fecha de entrega');
      return;
    }



    const newOrder = {
      id: Date.now(),
      customer: customerInfo,
      items: orderItems,
      total: getTotalOrder(),
      status: 'Pendiente',
      createdAt: new Date().toLocaleDateString(),
      deliveryDate: customerInfo.deliveryDate
    };


    // Guardar imágenes de los productos con logo
  // Guardar imágenes de cada producto
  orderItems.forEach((item, index) => {
    saveProductImage(item, newOrder.id, index);
  });

  // Exportar Excel
  exportOrderToExcel(newOrder);


    // Simulate saving to backend
    console.log('Orden guardada:', newOrder);
    alert('Orden de trabajo creada exitosamente');

    // Reset form
    setOrderItems([]);
    setCustomerInfo({
      companyName: '',
      contact: '',
      phone: '',
      email: '',
      deliveryDate: ''
    });
  };


const exportOrderToExcel = async (order: Order) => {
  const workbook = new ExcelJS.Workbook();
  const worksheet = workbook.addWorksheet("Ordenes");

  // Encabezados
  worksheet.columns = [
    { header: "ID", key: "id", width: 10 },
    { header: "CompanyName", key: "companyName", width: 20 },
    { header: "Contact", key: "contact", width: 20 },
    { header: "Direction", key: "direction", width: 20 },
    { header: "Email", key: "email", width: 25 },
    { header: "Phone", key: "phone", width: 15 },
    { header: "DeliveryDate", key: "deliveryDate", width: 15 },
    { header: "ProductName", key: "productName", width: 20 },
    { header: "LinkImg", key: "linkImg", width: 30 },
    { header: "Color", key: "color", width: 15 },
    { header: "Size", key: "size", width: 10 },
    { header: "Quantity", key: "quantity", width: 10 },
    { header: "BasePrice", key: "basePrice", width: 15 },
    { header: "Subtotal", key: "subtotal", width: 15 },
    { header: "Status", key: "status", width: 15 },
  ];

  // Filas por cada item
  order.items.forEach((item, index) => {
    worksheet.addRow({
      id: order.id + "-" + (index + 1),
      companyName: order.customer.companyName,
      contact: order.customer.contact,
      direction: "-", // aún no tienes campo en CustomerInfo
      email: order.customer.email,
      phone: order.customer.phone,
      deliveryDate: order.deliveryDate,
      productName: item.product.name,
      linkImg: './src/imgs/order-item/${order.id}_${index + 1}.png',
      color: item.color,
      size: item.size,
      quantity: item.quantity,
      basePrice: item.product.basePrice,
      subtotal: item.subtotal,
      status: order.status,
    });
  });

  // Exportar
  const buffer = await workbook.xlsx.writeBuffer();
  saveAs(new Blob([buffer]), "orderData.xlsx");
};

const saveProductImage = (item: OrderItem, orderId: number, index: number) => {
  const canvas = document.createElement("canvas");
  const ctx = canvas.getContext("2d");

  const baseImage = new Image();
  baseImage.src = item.product.image;

  baseImage.onload = () => {
    canvas.width = baseImage.width;
    canvas.height = baseImage.height;

    ctx?.drawImage(baseImage, 0, 0);

    if (item.logo) {
      const logoImg = new Image();
      logoImg.src = item.logo;
      logoImg.onload = () => {
        ctx?.drawImage(logoImg, item.logoPosition.x, item.logoPosition.y, 50, 50);
        canvas.toBlob((blob) => {
          if (blob) {
            saveAs(blob, './src/imgs/order-item/${orderId}_${index + 1}.png');
          }
        });
      };
    }
  };
};


interface ProductPreviewProps {
  products: products | null;
  color: string;
  logo: string | null;
  logoPos: { x: number; y: number };
}

const ProductPreview = ({ products, color, logo, logoPos }: ProductPreviewProps) => {
  if (!products) {
    return (
      <div className="w-full h-80 rounded-lg bg-gray-100 flex items-center justify-center">
        <p className="text-gray-400">Selecciona un producto</p>
      </div>
    );
  }
  return (
    <div className="w-full h-80 rounded-lg flex items-center justify-center relative p-4 bg-gray-100">
      <div 
        className="relative w-full h-full" 
        style={{ backgroundColor: color, borderRadius: '8px' }}
        onClick={handleLogoPosition}
      >
        <img
          src={products.image}
          alt={products.name}
          className="w-full h-full object-contain"
        />
        {logo && ( 
          <img
            src={logo}
            alt="Logo del cliente"
            style={{
              position: 'absolute',
              top: logoPos.y,
              left: logoPos.x,
              maxWidth: '30px',
              maxHeight: '30px',
              transform: 'translate(-50%, -50%)',
            }}
          />
        )}
      </div>
    </div>
  );
};


  const OrderSummary = () => (
    <div className="bg-white rounded-lg shadow-lg p-6">
      <h3 className="text-xl font-bold mb-4 flex items-center">
        <FileText className="mr-2" />
        Resumen de la Orden
      </h3>

      {orderItems.length === 0 ? (
        <p className="text-gray-500 text-center py-8">
          No hay productos en la orden
        </p>
      ) : (
        <>
          {orderItems.map((item) => (
            <div key={item.id} className="border-b py-4 last:border-b-0">
              <div className="flex justify-between items-start">
                <div className="flex-1">
                  <h4 className="font-semibold">{item.product.name}</h4>
                  <p className="text-sm text-gray-600">
                    Color: <span 
                      className="inline-block w-4 h-4 rounded-full ml-1"
                      style={{ backgroundColor: item.color }}
                    ></span>
                  </p>
                  <p className="text-sm text-gray-600">Tamaño: {item.size}</p>
                  <p className="text-sm text-gray-600">Cantidad: 
                    <input
                      type="number"
                      value={item.quantity}
                      onChange={(e) => updateOrderItem(item.id, 'quantity', parseInt(e.target.value))}
                      className="ml-2 w-16 px-2 py-1 border rounded"
                      min="1"
                    />
                  </p>
                  {item.logo && <p className="text-sm text-green-600">✓ Con logo personalizado</p>}
                </div>
                <div className="text-right">
                  <p className="font-semibold">${item.subtotal.toLocaleString()}</p>
                  <button
                    onClick={() => removeOrderItem(item.id)}
                    className="text-red-500 hover:text-red-700 mt-2"
                  >
                    <Trash2 size={16} />
                  </button>
                </div>
              </div>
            </div>
          ))}

          <div className="mt-4 pt-4 border-t">
            <div className="flex justify-between items-center text-xl font-bold">
              <span>Total:</span>
              <span>${getTotalOrder().toLocaleString()}</span>
            </div>
          </div>
        </>
      )}
    </div>
  );

  if (currentView === 'orders') {
    return (
      <div className="min-h-screen bg-gray-50 p-6">
        <div className="max-w-7xl mx-auto">
          {/* Header */}
          <div className="flex justify-between items-center mb-8">
            <div>
              <h1 className="text-3xl font-bold text-gray-900">Gestión de Órdenes</h1>
              <p className="text-gray-600">Visualiza y gestiona todas las órdenes de trabajo</p>
            </div>
            <button
              onClick={() => setCurrentView('new-order')}
              className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 flex items-center"
            >
              <Plus className="mr-2" size={20} />
              Nueva Orden
            </button>
          </div>

          {/* Stats Cards */}
          <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
            <div className="bg-white p-6 rounded-lg shadow-lg">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-gray-500">Pendientes</p>
                  <p className="text-3xl font-bold text-orange-600">{orders.pending}</p>
                </div>
                <Package className="h-12 w-12 text-orange-600" />
              </div>
            </div>
            <div className="bg-white p-6 rounded-lg shadow-lg">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-gray-500">En Proceso</p>
                  <p className="text-3xl font-bold text-blue-600">{orders.inProcess}</p>
                </div>
                <Edit3 className="h-12 w-12 text-blue-600" />
              </div>
            </div>
            <div className="bg-white p-6 rounded-lg shadow-lg">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-gray-500">Completadas</p>
                  <p className="text-3xl font-bold text-green-600">{orders.completed}</p>
                </div>
                <Eye className="h-12 w-12 text-green-600" />
              </div>
            </div>
            <div className="bg-white p-6 rounded-lg shadow-lg">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-gray-500">Ventas Hoy</p>
                  <p className="text-3xl font-bold text-purple-600">${orders.todaySales.toLocaleString()}</p>
                </div>
                <ShoppingCart className="h-12 w-12 text-purple-600" />
              </div>
            </div>
          </div>

          {/* Orders Table */}
          <div className="bg-white rounded-lg shadow-lg overflow-hidden">
            <div className="px-6 py-4 border-b">
              <h3 className="text-lg font-semibold">Órdenes Recientes</h3>
            </div>
            <div className="overflow-x-auto">
              <table className="min-w-full divide-y divide-gray-200">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">ID</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Cliente</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Fecha</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Estado</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Total</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Acciones</th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  <tr>
                    <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">#001</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">Empresa ABC</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">21/08/2025</td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <span className="px-2 py-1 text-xs font-semibold rounded-full bg-orange-100 text-orange-800">
                        Pendiente
                      </span>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">$1,500</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                      <button className="text-blue-600 hover:text-blue-900 mr-2">Ver</button>
                      <button className="text-green-600 hover:text-green-900">Editar</button>
                    </td>
                  </tr>
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 p-6">
      <div className="max-w-7xl mx-auto">
        {/* Navigation */}
        <div className="mb-8">
          <nav className="flex space-x-8">
            <button 
              onClick={() => setCurrentView('new-order')}
              className={`pb-2 border-b-2 font-medium ${currentView === 'new-order' ? 'border-blue-500 text-blue-600' : 'border-transparent text-gray-500'}`}
            >
              Nueva Orden
            </button>
            <button 
              onClick={() => setCurrentView('orders')}
              className={`pb-2 border-b-2 font-medium ${currentView === 'orders' ? 'border-blue-500 text-blue-600' : 'border-transparent text-gray-500'}`}
            >
              Gestión de Órdenes
            </button>
          </nav>
        </div>

        {/* Header */}
        <div className="flex justify-between items-center mb-8">
          <div>
            <h1 className="text-3xl font-bold text-gray-900">Nueva Orden de Trabajo</h1>
            <p className="text-gray-600">Dashboard Principal - Crea una nueva orden con diseño personalizado</p>
          </div>
          <div className="flex space-x-4">
            <button 
              onClick={() => setCurrentView('orders')}
              className="bg-gray-600 text-white px-4 py-2 rounded-lg hover:bg-gray-700 flex items-center"
            >
              <Eye className="mr-2" size={20} />
              Ver Órdenes
            </button>
            <button 
              onClick={saveOrder}
              className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 flex items-center"
            >
              <Save className="mr-2" size={20} />
              Guardar Orden
            </button>
          </div>
        </div>

        {/* Stats */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
          <div className="bg-white p-6 rounded-lg shadow-lg">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-gray-500">Pendientes</p>
                <p className="text-3xl font-bold text-orange-600">{orders.pending}</p>
              </div>
              <Package className="h-8 w-8 text-orange-600" />
            </div>
          </div>
          <div className="bg-white p-6 rounded-lg shadow-lg">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-gray-500">En Proceso</p>
                <p className="text-3xl font-bold text-blue-600">{orders.inProcess}</p>
              </div>
              <Edit3 className="h-8 w-8 text-blue-600" />
            </div>
          </div>
          <div className="bg-white p-6 rounded-lg shadow-lg">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-gray-500">Completadas</p>
                <p className="text-3xl font-bold text-green-600">{orders.completed}</p>
              </div>
              <Eye className="h-8 w-8 text-green-600" />
            </div>
          </div>
          <div className="bg-white p-6 rounded-lg shadow-lg">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-gray-500">Ventas Hoy</p>
                <p className="text-3xl font-bold text-purple-600">${orders.todaySales.toLocaleString()}</p>
              </div>
              <ShoppingCart className="h-8 w-8 text-purple-600" />
            </div>
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Left Column */}
          <div className="space-y-8">
            {/* Product Selection */}
            {/* Sección de Selección de Producto */}
                  <div className="bg-white p-6 rounded-lg shadow">
                    <h3 className="text-lg font-semibold mb-4 text-gray-800">
                      1. Selección de Producto
                    </h3>
                                          {/* Contenedor del Carrusel */}
                      <div className="flex items-center justify-between mt-4">
                        {/* Botón de navegación izquierda */}
                        <button
                          onClick={() => setCurrentProductIndex(prev => (prev - 1 + products.length) % products.length)}
                          disabled={products.length <= 1}
                          className="text-gray-500 hover:text-gray-800 disabled:opacity-50"
                        >
                          <ChevronLeft size={24} />
                        </button>

                        {/* Tarjeta del producto actual */}
                        {products.length > 0 && (
                          <div
                            key={products[currentProductIndex].id}
                            onClick={() => setSelectedProduct(products[currentProductIndex])}
                            className={`p-4 rounded-lg cursor-pointer w-80 h-96 transition-all duration-200 ${
                              selectedProduct?.id === products[currentProductIndex].id
                                ? 'bg-blue-100 ring-2 ring-blue-500'
                                : 'hover:bg-gray-50'
                            }`}
                          >
                          <div className="w-full h-full bg-gray-200 rounded-lg flex items-center justify-center overflow-hidden">
                              <img
                                src={products[currentProductIndex].image}
                                alt={products[currentProductIndex].name}
                                className="w-full h-full object-contain"
                              />
                            </div>
                            <div className="text-center mt-4">
                              <p className="text-sm font-medium text-center text-gray-700">
                                {products[currentProductIndex].name}
                              </p>
                              <p className="text-xs text-gray-500 text-center">
                                ${products[currentProductIndex].basePrice.toLocaleString()} MXN
                              </p>
                            </div>
                            </div>
                        )}

                        {/* Botón de navegación derecha */}
                        <button
                          onClick={() => setCurrentProductIndex(prev => (prev + 1) % products.length)}
                          disabled={products.length <= 1}
                          className="text-gray-500 hover:text-gray-800 disabled:opacity-50"
                        >
                          <ChevronRight size={24} />
                        </button>
                      </div>
                    </div>

            {/* Add Product to Order */}
            <div className="bg-white rounded-lg shadow-lg p-6">
              <h3 className="text-xl font-bold mb-4">Agregar Producto a la Orden</h3>

              <div className="grid grid-cols-2 gap-4 mb-6">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Cantidad</label>
                  <input
                    type="number"
                    value={quantity}
                    onChange={(e) => setQuantity(Number(e.target.value))}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    min="1"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Tamaño</label>
                  <select
                    value={selectedSize}
                    onChange={(e) => setSelectedSize(e.target.value)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  >
                    {sizes.map((size) => (
                      <option key={size} value={size}>{size}</option>
                    ))}
                  </select>
                </div>
              </div>

              <div className="mb-6">
                <label className="block text-sm font-medium text-gray-700 mb-2">Colores</label>
                <div className="grid grid-cols-10 gap-2">
                  {colors.map((color) => (
                    <button
                      key={color}
                      className={`w-8 h-8 rounded-full border-2 ${
                        selectedColor === color ? 'border-gray-800 scale-110' : 'border-gray-300'
                      }`}
                      style={{ backgroundColor: color }}
                      onClick={() => setSelectedColor(color)}
                    />
                  ))}
                </div>
              </div>

              <button
                onClick={addProductToOrder}
                disabled={!selectedProduct}
                className="w-full bg-blue-600 text-white py-3 px-4 rounded-lg hover:bg-blue-700 disabled:bg-gray-300 flex items-center justify-center"
              >
                <Plus className="mr-2" size={20} />
                Agregar
              </button>
            </div>

            {/* Customer Information */}
            <div className="bg-white rounded-lg shadow-lg p-6">
              <h3 className="text-xl font-bold mb-4 flex items-center">
                <User className="mr-2" />
                Información del Cliente
              </h3>

              <div className="grid grid-cols-1 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Nombre del Cliente</label>
                  <input
                    type="text"
                    value={customerInfo.companyName}
                    onChange={(e) => setCustomerInfo({...customerInfo, companyName: e.target.value})}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    placeholder="Empresa ABC"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Contacto</label>
                  <input
                    type="text"
                    value={customerInfo.contact}
                    onChange={(e) => setCustomerInfo({...customerInfo, contact: e.target.value})}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    placeholder="Juan Pérez"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Teléfono</label>
                  <input
                    type="tel"
                    value={customerInfo.phone}
                    onChange={(e) => setCustomerInfo({...customerInfo, phone: e.target.value})}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    placeholder="555-1234"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Email</label>
                  <input
                    type="email"
                    value={customerInfo.email}
                    onChange={(e) => setCustomerInfo({...customerInfo, email: e.target.value})}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    placeholder="cliente@empresa.com"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Fecha de Entrega</label>
                  <input
                    type="date"
                    value={customerInfo.deliveryDate}
                    onChange={(e) => setCustomerInfo({...customerInfo, deliveryDate: e.target.value})}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  />
                </div>
              </div>
            </div>
          </div>

          {/* Right Column */}
          <div className="space-y-8">
            {/* Design Editor */}
            <div className="bg-white rounded-lg shadow-lg p-6">
              <h3 className="text-xl font-bold mb-4">Editor de Diseño</h3>
              <p className="text-gray-600 mb-4">Sube tu logo y posiciónalo en la prenda</p>

              <div className="mb-6">
                <label className="block text-sm font-medium text-gray-700 mb-2">Subir Logo</label>
                <div className="mt-1 flex justify-center px-6 pt-5 pb-6 border-2 border-gray-300 border-dashed rounded-md">
                  <div className="space-y-1 text-center">
                    <Upload className="mx-auto h-12 w-12 text-gray-400" />
                    <div className="flex text-sm text-gray-600">
                      <label className="relative cursor-pointer bg-white rounded-md font-medium text-blue-600 hover:text-blue-500">
                        <span>Upload a file</span>
                        <input
                          ref={fileInputRef}
                          type="file"
                          className="sr-only"
                          accept="image/*"
                          onChange={handleLogoUpload}
                        />
                      </label>
                    </div>
                    <p className="text-xs text-gray-500">PNG, JPG, GIF hasta 10MB</p>
                  </div>
                </div>
              </div>

              <ProductPreview 
                products={selectedProduct} 
                color={selectedColor} 
                logo={logoFile} 
                logoPos={logoPosition} 
              />

              {selectedProduct && (
                <div className="mt-4 text-center">
                  <p className="text-sm text-gray-600">
                    {logoFile 
                      ? 'Haz clic en la prenda para posicionar el logo' 
                      : 'Selecciona un producto para ver la vista previa'}
                  </p>
                </div>
              )}
            </div>

            {/* Order Summary */}
            <OrderSummary />
          </div>
        </div>
      </div>
    </div>
  );
};

export default OrderManagementSystem;
