import React, { useState, useRef, useCallback, ChangeEvent, useEffect, Ref } from 'react';

import {
  ShoppingCart,
  Upload,
  Edit3,
  Save,
  Trash2,
  Eye,
  Plus,
  FileText,
  Calendar,
  User,
  Phone,
  Mail,
  Package,
  ChevronLeft,
  ChevronRight
} from 'lucide-react';

// Mock CSV data since we can't import actual files
const mockProductsData = `ID,name,basePrice,image,type
1,Camiseta Básica,25.50,camiseta-basica.jpg,Camisetas
2,Polo Premium,35.00,polo-premium.jpg,Polos  
3,Sudadera Clásica,45.75,sudadera-clasica.jpg,Sudaderas
4,Gorra Deportiva,18.25,gorra-deportiva.jpg,Gorras
5,Chaqueta Ejecutiva,85.00,chaqueta-ejecutiva.jpg,Chaquetas`;

interface Product {
  id: number;
  name: string;
  basePrice: number;
  image: string;
  type: string;
}

interface OrderItem {
  id: number;
  product: Product;
  color: string;
  size: string;
  quantity: number;
  logo: string | null;
  logoPosition: { x: number; y: number };
  subtotal: number;
  linkImg?: string; // ✅ Agregamos linkImg para las imágenes generadas
}

interface CustomerInfo {
  companyName: string;
  contact: string;
  phone: string;
  email: string;
  deliveryDate: string;
  direction?: string; // ✅ Agregamos dirección que faltaba
}

interface Order {
  id: number;
  customer: CustomerInfo;
  items: OrderItem[];
  total: number;
  status: string;
  createdAt: string;
  deliveryDate: string;
}

interface OrderSummaryProps {
  orderItems: OrderItem[];
  updateOrderItem: (id: number, field: keyof OrderItem, value: any) => void;
  removeOrderItem: (id: number) => void;
  getTotalOrder: () => number;
}

interface ProductPreviewProps {
  product: Product | null;
  color: string;
  logo: string | null;
  logoPos: { x: number; y: number };
  handleLogoPosition?: (event: React.MouseEvent<HTMLDivElement>) => void;
  previewId?: string; // ✅ Para identificar únicamente cada preview
}

const ProductPreview = React.forwardRef<HTMLDivElement, ProductPreviewProps & { imageUrl: string | undefined }>((
  { product, color, logo, logoPos, handleLogoPosition, imageUrl, previewId }, 
  ref
) => {
    if (!product || !imageUrl) {
        return (
            <div className="w-full h-80 rounded-lg bg-gray-100 flex items-center justify-center">
                <p className="text-gray-400">Selecciona un producto</p>
            </div>
        );
    }

    return (
        <div
            ref={ref}
            id={previewId} // ✅ Asignamos el ID único para captura
            className="w-full h-80 rounded-lg flex items-center justify-center relative p-4 bg-gray-100"
        >
            <div
                className="relative w-full h-full"
                style={{ backgroundColor: color, borderRadius: '8px' }}
                onClick={handleLogoPosition}
            >
                <img
                    src={imageUrl}
                    alt={product.name}
                    className="w-full h-full object-contain"
                    crossOrigin="anonymous" // ✅ Para evitar problemas de CORS con html2canvas
                />
                {logo && (
                    <img
                        src={logo}
                        alt="Logo del cliente"
                        crossOrigin="anonymous" // ✅ Para evitar problemas de CORS
                        style={{
                            position: 'absolute',
                            top: `${logoPos.y}%`,
                            left: `${logoPos.x}%`,
                            maxWidth: '40px',
                            maxHeight: '40px',
                            transform: 'translate(-50%, -50%)',
                        }}
                    />
                )}
            </div>
        </div>
    );
});

const OrderSummary = ({ orderItems, updateOrderItem, removeOrderItem, getTotalOrder }: OrderSummaryProps) => (
  <div className="bg-white rounded-lg shadow-lg p-6">
    <h3 className="text-xl font-bold mb-4 flex items-center">
      <FileText className="mr-2" />
      Resumen de la Orden
    </h3>
    {orderItems.length === 0 ? (
      <p className="text-gray-500 text-center py-8">
        No hay productos en la orden
      </p>
    ) : (
      <>
        {orderItems.map((item) => (
          <div key={item.id} className="border-b py-4 last:border-b-0">
            <div className="flex justify-between items-start">
              <div className="flex-1">
                <h4 className="font-semibold">{item.product.name}</h4>
                <p className="text-sm text-gray-600">
                  Color: <span
                    className="inline-block w-4 h-4 rounded-full ml-1"
                    style={{ backgroundColor: item.color }}
                  ></span>
                </p>
                <p className="text-sm text-gray-600">Tamaño: {item.size}</p>
                <p className="text-sm text-gray-600">Cantidad:
                  <input
                    type="number"
                    value={item.quantity}
                    onChange={(e) => {
                      const newQuantity = parseInt(e.target.value) || 1;
                      updateOrderItem(item.id, 'quantity', newQuantity);
                      // ✅ Actualizar subtotal automáticamente
                      updateOrderItem(item.id, 'subtotal', newQuantity * item.product.basePrice);
                    }}
                    className="ml-2 w-16 px-2 py-1 border rounded"
                    min="1"
                  />
                </p>
                {item.logo && <p className="text-sm text-green-600">✓ Con logo personalizado</p>}

                {/* ✅ Vista previa del item en el resumen */}
                <div className="mt-2">
                  <div 
                    id={`preview-${item.id}`} 
                    className="w-32 h-32 border rounded relative bg-gray-50"
                  >
                    <div
                      className="w-full h-full relative"
                      style={{ backgroundColor: item.color, borderRadius: '4px' }}
                    >
                      <img
                        src={`https://via.placeholder.com/150x150/cccccc/666666?text=${encodeURIComponent(item.product.name)}`}
                        alt={item.product.name}
                        className="w-full h-full object-contain"
                        crossOrigin="anonymous"
                      />
                      {item.logo && (
                        <img
                          src={item.logo}
                          alt="Logo"
                          crossOrigin="anonymous"
                          style={{
                            position: 'absolute',
                            top: `${item.logoPosition.y}%`,
                            left: `${item.logoPosition.x}%`,
                            maxWidth: '20px',
                            maxHeight: '20px',
                            transform: 'translate(-50%, -50%)',
                          }}
                        />
                      )}
                    </div>
                  </div>
                </div>
              </div>
              <div className="text-right">
                <p className="font-semibold">${item.subtotal.toLocaleString()}</p>
                <button
                  onClick={() => removeOrderItem(item.id)}
                  className="text-red-500 hover:text-red-700 mt-2"
                >
                  <Trash2 size={16} />
                </button>
              </div>
            </div>
          </div>
        ))}
        <div className="mt-4 pt-4 border-t">
          <div className="flex justify-between items-center text-xl font-bold">
            <span>Total:</span>
            <span>${getTotalOrder().toLocaleString()}</span>
          </div>
        </div>
      </>
    )}
  </div>
);

// ✅ Función para parsear CSV
const parseCSV = (csvText: string): Product[] => {
  const lines = csvText.trim().split('\n');
  const headers = lines[0].split(',');
  const products: Product[] = [];

  for (let i = 1; i < lines.length; i++) {
    const values = lines[i].split(',');
    const product = {
      id: parseInt(values[0]) || i,
      name: values[1] || `Producto ${i}`,
      basePrice: parseFloat(values[2]) || 0,
      image: values[3] || 'default.jpg',
      type: values[4] || 'General'
    };
    products.push(product);
  }

  return products;
};

const OrderManagementSystem = () => {
  const [orders, setOrders] = useState({
    pending: 12,
    inProcess: 8,
    completed: 45,
    todaySales: 8430
  });

  const [products, setProducts] = useState<Product[]>([]);
  const [selectedProduct, setSelectedProduct] = useState<Product | null>(null);
  const [selectedColor, setSelectedColor] = useState('#FFFFFF');
  const [selectedSize, setSelectedSize] = useState('M');
  const [quantity, setQuantity] = useState(1);
  const [logoFile, setLogoFile] = useState<string | null>(null);
  const [logoPosition, setLogoPosition] = useState({ x: 50, y: 50 });
  const [orderItems, setOrderItems] = useState<OrderItem[]>([]);
  const [customerInfo, setCustomerInfo] = useState<CustomerInfo>({
    companyName: '',
    contact: '',
    phone: '',
    email: '',
    deliveryDate: '',
    direction: '' // ✅ Inicializamos dirección
  });

  const [isLoading, setIsLoading] = useState(false); // ✅ Estado para controlar carga

  const productPreviewRef = useRef<HTMLDivElement>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);

  // ✅ Cargar productos desde mock data
  useEffect(() => {
    const parsedProducts = parseCSV(mockProductsData);
    setProducts(parsedProducts);
    if (parsedProducts.length > 0) {
      setSelectedProduct(parsedProducts[0]);
    }
  }, []);

  const handleAddOrderItem = () => {
    if (selectedProduct && quantity > 0) {
      const newOrderItem: OrderItem = {
        id: Date.now(),
        product: selectedProduct,
        color: selectedColor, // ✅ Usar el color seleccionado
        size: selectedSize,
        quantity: quantity,
        logo: logoFile,
        logoPosition: logoPosition,
        subtotal: selectedProduct.basePrice * quantity
      };

      setOrderItems([...orderItems, newOrderItem]);

      // ✅ Reset solo los campos necesarios, mantenemos el producto seleccionado
      setSelectedColor('#FFFFFF');
      setSelectedSize('M');
      setQuantity(1);
      setLogoFile(null);
      setLogoPosition({ x: 50, y: 50 });

      // ✅ Limpiar input de archivo
      if (fileInputRef.current) {
        fileInputRef.current.value = '';
      }
    }
  };

  const handleProductSelect = (product: Product) => {
    setSelectedProduct(product);
  };

  const handleLogoUpload = (event: ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setLogoFile(reader.result as string);
        setLogoPosition({ x: 50, y: 50 });
      };
      reader.readAsDataURL(file);
    }
  };

  const handleLogoPosition = useCallback((event: React.MouseEvent<HTMLDivElement>) => {
    const element = event.currentTarget;
    const rect = element.getBoundingClientRect();
    const x = ((event.clientX - rect.left) / rect.width) * 100;
    const y = ((event.clientY - rect.top) / rect.height) * 100;
    setLogoPosition({ x, y });
  }, []);

  const handleCustomerInfoChange = (e: ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setCustomerInfo({ ...customerInfo, [name]: value });
  };

  const updateOrderItem = (id: number, field: keyof OrderItem, value: any) => {
    setOrderItems(orderItems.map(item =>
      item.id === id ? { ...item, [field]: value } : item
    ));
  };

  const removeOrderItem = (id: number) => {
    setOrderItems(orderItems.filter(item => item.id !== id));
  };

  const getTotalOrder = () => {
    return orderItems.reduce((total, item) => total + item.subtotal, 0);
  };

  // ✅ Función auxiliar para capturar imagen de un elemento
  const captureElementAsImage = async (elementId: string): Promise<string | null> => {
    try {
      const element = document.getElementById(elementId);
      if (!element) {
        console.error(`Elemento no encontrado: ${elementId}`);
        return null;
      }

      // ✅ Configuración mejorada para html2canvas
      const canvas = await (window as any).html2canvas(element, {
        scale: 2,
        useCORS: true,
        allowTaint: true,
        backgroundColor: null,
        logging: false,
        width: element.offsetWidth,
        height: element.offsetHeight
      });

      return canvas.toDataURL('image/png', 0.9);
    } catch (error) {
      console.error(`Error capturando imagen de ${elementId}:`, error);
      return null;
    }
  };

  const saveOrder = async () => {
    if (orderItems.length === 0) {
      alert("No puedes guardar una orden sin productos.");
      return;
    }

    // ✅ Validar información del cliente
    if (!customerInfo.companyName || !customerInfo.contact || !customerInfo.email) {
      alert("Por favor completa la información del cliente (Empresa, Contacto y Email son obligatorios).");
      return;
    }

    setIsLoading(true);

    try {
      const orderId = Date.now();
      const deliveryDateFormatted = customerInfo.deliveryDate || new Date(Date.now() + 7 * 24 * 60 * 60 * 1000).toISOString().split('T')[0]; // ✅ Default a 7 días

      const orderToSave = {
        id: orderId,
        customer: {
          ...customerInfo,
          deliveryDate: deliveryDateFormatted,
          direction: customerInfo.direction || 'N/A' // ✅ Asegurar que direction existe
        },
        items: orderItems,
        total: getTotalOrder(),
        status: 'Pendiente',
        createdAt: new Date().toISOString(),
        deliveryDate: deliveryDateFormatted
      };

      console.log('🔄 Iniciando guardado de orden:', orderId);

      // ✅ Capturar imágenes de todos los items
      const imagesToUpload: { itemId: number; imageData: string }[] = [];

      for (const item of orderItems) {
        const imageData = await captureElementAsImage(`preview-${item.id}`);
        if (imageData) {
          imagesToUpload.push({ itemId: item.id, imageData });
          console.log(`📸 Imagen capturada para item ${item.id}`);
        } else {
          console.warn(`⚠️ No se pudo capturar imagen para item ${item.id}`);
        }
      }

      console.log(`📦 Total de imágenes capturadas: ${imagesToUpload.length}`);

      // ✅ Enviar al servidor
      const response = await fetch('http://localhost:3001/save-order', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          order: orderToSave,
          images: imagesToUpload,
        }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || `Error del servidor: ${response.statusText}`);
      }

      const result = await response.json();
      console.log('✅ Respuesta del servidor:', result);

      alert(`Orden guardada exitosamente!\nID: ${orderId}\nProductos: ${result.itemsProcessed}\nImágenes: ${result.imagesProcessed}`);

      // ✅ Limpiar formulario después de guardar exitosamente
      setOrderItems([]);
      setCustomerInfo({
        companyName: '',
        contact: '',
        phone: '',
        email: '',
        deliveryDate: '',
        direction: ''
      });
      setLogoFile(null);
      if (fileInputRef.current) {
        fileInputRef.current.value = '';
      }

    } catch (error) {
      console.error('❌ Error al guardar la orden:', error);
      alert(`Ocurrió un error al guardar la orden: ${error instanceof Error ? error.message : 'Error desconocido'}`);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="bg-gray-100 min-h-screen p-8 font-sans">
      <div className="container mx-auto">
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-3xl font-bold text-gray-800">Sistema de Órdenes</h1>
          <div className="flex space-x-4 text-sm font-semibold text-gray-700">
            <div className="flex items-center space-x-2">
              <span className="text-red-500">{orders.pending}</span>
              <span>Pendientes</span>
            </div>
            <div className="flex items-center space-x-2">
              <span className="text-yellow-500">{orders.inProcess}</span>
              <span>En Proceso</span>
            </div>
            <div className="flex items-center space-x-2">
              <span className="text-green-500">{orders.completed}</span>
              <span>Completadas</span>
            </div>
          </div>
        </div>

        <div className="bg-white rounded-xl shadow-xl p-8">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
            <div className="col-span-1 lg:col-span-1 bg-gray-50 rounded-lg p-6 shadow-sm">
              <h2 className="text-xl font-bold text-gray-800 mb-4 flex items-center">
                <User className="mr-2" />
                Información del Cliente
              </h2>
              <div className="space-y-4">
                <div>
                  <label htmlFor="companyName" className="text-sm font-medium text-gray-700 block mb-1">
                    Empresa <span className="text-red-500">*</span>
                  </label>
                  <div className="relative">
                    <User className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400" size={18} />
                    <input
                      type="text"
                      id="companyName"
                      name="companyName"
                      value={customerInfo.companyName}
                      onChange={handleCustomerInfoChange}
                      className="w-full pl-10 pr-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                      placeholder="Nombre de la empresa"
                      required
                    />
                  </div>
                </div>
                <div>
                  <label htmlFor="contact" className="text-sm font-medium text-gray-700 block mb-1">
                    Contacto <span className="text-red-500">*</span>
                  </label>
                  <div className="relative">
                    <User className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400" size={18} />
                    <input
                      type="text"
                      id="contact"
                      name="contact"
                      value={customerInfo.contact}
                      onChange={handleCustomerInfoChange}
                      className="w-full pl-10 pr-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                      placeholder="Nombre del contacto"
                      required
                    />
                  </div>
                </div>
                <div>
                  <label htmlFor="phone" className="text-sm font-medium text-gray-700 block mb-1">Teléfono</label>
                  <div className="relative">
                    <Phone className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400" size={18} />
                    <input
                      type="tel"
                      id="phone"
                      name="phone"
                      value={customerInfo.phone}
                      onChange={handleCustomerInfoChange}
                      className="w-full pl-10 pr-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                      placeholder="Número de teléfono"
                    />
                  </div>
                </div>
                <div>
                  <label htmlFor="email" className="text-sm font-medium text-gray-700 block mb-1">
                    Email <span className="text-red-500">*</span>
                  </label>
                  <div className="relative">
                    <Mail className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400" size={18} />
                    <input
                      type="email"
                      id="email"
                      name="email"
                      value={customerInfo.email}
                      onChange={handleCustomerInfoChange}
                      className="w-full pl-10 pr-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                      placeholder="Email de contacto"
                      required
                    />
                  </div>
                </div>
                <div>
                  <label htmlFor="direction" className="text-sm font-medium text-gray-700 block mb-1">Dirección</label>
                  <div className="relative">
                    <Package className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400" size={18} />
                    <input
                      type="text"
                      id="direction"
                      name="direction"
                      value={customerInfo.direction}
                      onChange={handleCustomerInfoChange}
                      className="w-full pl-10 pr-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                      placeholder="Dirección de entrega"
                    />
                  </div>
                </div>
                <div>
                  <label htmlFor="deliveryDate" className="text-sm font-medium text-gray-700 block mb-1">Fecha de Entrega</label>
                  <div className="relative">
                    <Calendar className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400" size={18} />
                    <input
                      type="date"
                      id="deliveryDate"
                      name="deliveryDate"
                      value={customerInfo.deliveryDate}
                      onChange={handleCustomerInfoChange}
                      className="w-full pl-10 pr-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                    />
                  </div>
                </div>
              </div>
            </div>

            <div className="col-span-1 md:col-span-1 lg:col-span-2 bg-white rounded-lg p-6 shadow-lg relative flex items-center justify-center">
              <h2 className="absolute top-6 left-6 text-xl font-bold mb-4 flex items-center">
                  <ShoppingCart className="mr-2" />
                  Selección de Producto
              </h2>

              {products.length > 0 && (
                <>
                  <button
                    onClick={() => {
                      const currentIndex = products.findIndex(p => p.id === selectedProduct?.id);
                      const newIndex = (currentIndex - 1 + products.length) % products.length;
                      setSelectedProduct(products[newIndex]);
                    }}
                    className="absolute left-2 top-1/2 transform -translate-y-1/2 bg-gray-200 p-2 rounded-full shadow-md hover:bg-gray-300 z-10"
                  >
                    <ChevronLeft size={24} />
                  </button>

                  <div className="flex flex-col items-center justify-center p-4">
                    {selectedProduct ? (
                      <div className="w-64 h-64 border-2 border-blue-500 ring-2 ring-blue-500 rounded-lg overflow-hidden flex items-center justify-center transition-all duration-300">
                        <img
                          src={`https://via.placeholder.com/300x300/cccccc/666666?text=${encodeURIComponent(selectedProduct.name)}`}
                          alt={selectedProduct.name}
                          className="w-full h-full object-contain"
                        />
                      </div>
                    ) : (
                      <div className="w-64 h-64 bg-gray-100 rounded-lg flex items-center justify-center">
                        <p className="text-gray-400">Selecciona un producto</p>
                      </div>
                    )}
                    <p className="text-lg font-semibold text-center mt-4">{selectedProduct?.name}</p>
                    <p className="text-md text-gray-700 text-center">${selectedProduct?.basePrice?.toLocaleString()}</p>
                  </div>

                  <button
                    onClick={() => {
                      const currentIndex = products.findIndex(p => p.id === selectedProduct?.id);
                      const newIndex = (currentIndex + 1) % products.length;
                      setSelectedProduct(products[newIndex]);
                    }}
                    className="absolute right-2 top-1/2 transform -translate-y-1/2 bg-gray-200 p-2 rounded-full shadow-md hover:bg-gray-300 z-10"
                  >
                    <ChevronRight size={24} />
                  </button>
                </>
              )}
              {products.length === 0 && (
                  <p className="text-gray-500 text-center py-8">
                    No hay productos disponibles.
                  </p>
              )}
            </div>

            <div className="col-span-1 md:col-span-2 lg:col-span-3 grid grid-cols-1 md:grid-cols-2 gap-8 mt-6">

              <div className="bg-white rounded-lg p-6 shadow-lg">
                <h3 className="text-xl font-bold mb-4 flex items-center">
                  <Edit3 className="mr-2" />
                  Detalles del Producto
                </h3>

                {selectedProduct && (
                  <div className="space-y-4">
                    <div className="flex items-center space-x-2">
                      <Package size={20} />
                      <p className="text-sm text-gray-700">Producto Seleccionado: <span className="font-semibold">{selectedProduct.name}</span></p>
                    </div>

                    <div>
                      <label htmlFor="color" className="text-sm font-medium text-gray-700 block mb-1">Color</label>
                      <input
                        type="color"
                        id="color"
                        value={selectedColor}
                        onChange={(e) => setSelectedColor(e.target.value)}
                        className="w-full h-10 border rounded-lg cursor-pointer"
                      />
                    </div>

                    <div>
                      <label htmlFor="size" className="text-sm font-medium text-gray-700 block mb-1">Talla</label>
                      <select
                        id="size"
                        value={selectedSize}
                        onChange={(e) => setSelectedSize(e.target.value)}
                        className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                      >
                        <option>XS</option>
                        <option>S</option>
                        <option>M</option>
                        <option>L</option>
                        <option>XL</option>
                      </select>
                    </div>

                    <div>
                      <label htmlFor="quantity" className="text-sm font-medium text-gray-700 block mb-1">Cantidad</label>
                      <input
                        type="number"
                        id="quantity"
                        value={quantity}
                        onChange={(e) => setQuantity(parseInt(e.target.value) || 1)}
                        className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                        min="1"
                      />
                    </div>

                    <div className="space-y-2">
                      <label className="text-sm font-medium text-gray-700">Logo del Cliente</label>
                      <div className="mt-1 flex justify-center px-6 pt-5 pb-6 border-2 border-gray-300 border-dashed rounded-md">
                        <div className="space-y-1 text-center">
                          {logoFile ? (
                            <img src={logoFile} alt="Logo" className="mx-auto h-12 w-12 text-gray-400" />
                          ) : (
                            <Upload className="mx-auto h-12 w-12 text-gray-400" />
                          )}
                          <div className="flex text-sm text-gray-600">
                            <label className="relative cursor-pointer bg-white rounded-md font-medium text-blue-600 hover:text-blue-500">
                              <span>Subir un archivo</span>
                              <input
                                ref={fileInputRef}
                                type="file"
                                className="sr-only"
                                accept="image/*"
                                onChange={handleLogoUpload}
                              />
                            </label>
                          </div>
                          <p className="text-xs text-gray-500">PNG, JPG, GIF hasta 10MB</p>
                        </div>
                      </div>
                    </div>

                    <button
                      onClick={handleAddOrderItem}
                      className="w-full bg-green-500 hover:bg-green-600 text-white font-bold py-2 px-4 rounded-lg flex items-center justify-center transition duration-200"
                      disabled={isLoading}
                    >
                      <Plus className="mr-2" />
                      Agregar a la Orden
                    </button>
                  </div>
                )}
              </div>

              <div className="bg-white rounded-lg p-6 shadow-lg flex flex-col items-center">
                <h3 className="text-xl font-bold mb-4 flex items-center">
                  <Eye className="mr-2" />
                  Vista Previa
                </h3>

                {selectedProduct ? (
                  <div className="w-full">
                    <ProductPreview
                      product={selectedProduct}
                      color={selectedColor}
                      logo={logoFile}
                      logoPos={logoPosition}
                      handleLogoPosition={handleLogoPosition}
                      imageUrl={`https://via.placeholder.com/300x300/cccccc/666666?text=${encodeURIComponent(selectedProduct.name)}`}
                      previewId="preview-selected"
                    />
                  </div>
                ) : (
                  <p className="text-gray-500">Selecciona un producto</p>
                )}

                <div className="mt-4 text-center">
                  <p className="text-sm text-gray-600">
                    {logoFile ? 'Haz clic en la prenda para posicionar el logo' : 'Sube un logo para posicionarlo'}
                  </p>
                </div>
              </div>
            </div>

            <div className="col-span-1 md:col-span-2 lg:col-span-3">
              <OrderSummary
                orderItems={orderItems}
                updateOrderItem={updateOrderItem}
                removeOrderItem={removeOrderItem}
                getTotalOrder={getTotalOrder}
              />
            </div>
          </div>
          <div className="mt-8 flex justify-end">
            <button
              onClick={saveOrder}
              className={`font-bold py-3 px-6 rounded-lg flex items-center transition duration-200 text-lg ${
                isLoading 
                  ? 'bg-gray-400 cursor-not-allowed' 
                  : 'bg-blue-600 hover:bg-blue-700 text-white'
              }`}
              disabled={isLoading}
            >
              <Save className="mr-2" />
              {isLoading ? 'Guardando...' : 'Guardar Orden'}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default OrderManagementSystem;
