import React, { useState, useRef, useCallback, ChangeEvent, useEffect, Ref } from 'react';
import html2canvas from 'html2canvas';

import {
  ShoppingCart,
  Upload,
  Edit3,
  Save,
  Trash2,
  Eye,
  Plus,
  FileText,
  Calendar,
  User,
  Phone,
  Mail,
  Package,
  ChevronLeft,
  ChevronRight
} from 'lucide-react';

import Papa, { ParseResult } from 'papaparse';
import productsCsv from './db/Products.csv';

// Interfaces
interface Product {
    id: number;
    name: string;
    basePrice: number;
    image: string;
    type: string;
}

interface OrderItem {
    id: number;
    product: Product;
    color: string;
    size: string;
    quantity: number;
    logo: string | null;
    logoPosition: { x: number; y: number };
    subtotal: number;
}

interface CustomerInfo {
    companyName: string;
    contact: string;
    phone: string;
    email: string;
    direction: string;
    deliveryDate: string;
}

interface Order {
    id: number;
    customer: CustomerInfo;
    items: OrderItem[];
    total: number;
    status: string;
    createdAt: string;
    deliveryDate: string;
}

interface OrderSummaryProps {
    orderItems: OrderItem[];
    updateOrderItem: (id: number, field: keyof OrderItem, value: any) => void;
    removeOrderItem: (id: number) => void;
    getTotalOrder: () => number;
}

interface ProductPreviewProps {
  product: Product | null;
  color: string;
  logo: string | null;
  logoPos: { x: number; y: number };
  handleLogoPosition: (e: React.MouseEvent<HTMLDivElement>) => void;
  imageUrl?: string;
}

// Componente para la vista previa del producto
const ProductPreview: React.FC<ProductPreviewProps> = ({ product, color, logo, logoPos, handleLogoPosition, imageUrl }) => {
  return (
    <div
      onClick={handleLogoPosition}
      className="relative w-full h-96 bg-gray-200 border rounded-lg overflow-hidden flex justify-center items-center cursor-crosshair"
      style={{ backgroundColor: color }}
    >
      {imageUrl && <img src={imageUrl} alt={product?.name} className="w-full h-full object-cover" />}
      {logo && (
        <img
          src={logo}
          alt="Logo"
          className="absolute"
          style={{ left: `${logoPos.x}%`, top: `${logoPos.y}%`, transform: 'translate(-50%, -50%)', width: '60px', height: '60px' }}
        />
      )}
    </div>
  );
};

// Componente para el resumen de la orden
const OrderSummary: React.FC<OrderSummaryProps> = ({ orderItems, updateOrderItem, removeOrderItem, getTotalOrder }) => (
    <div className="bg-gray-50 p-6 rounded-lg shadow-inner">
      <h2 className="text-xl font-bold mb-4 flex items-center">
        <ShoppingCart className="mr-2" /> Resumen de la Orden
      </h2>
      {orderItems.length === 0 ? (
        <p className="text-gray-500">No hay productos en la orden.</p>
      ) : (
        <div>
          {orderItems.map((it: OrderItem) => (
            <div key={it.id} className="flex items-center space-x-4 mb-4 border-b pb-4">
              <div className="flex-grow">
                <div className="font-semibold">{it.product.name}</div>
                <div className="text-sm text-gray-500">
                  Color: {it.color}, Talla: {it.size}
                </div>
              </div>
              <div className="text-right">
                <div className="font-semibold">${it.subtotal}</div>
                <button onClick={() => removeOrderItem(it.id)} className="text-red-500 mt-2">
                  <Trash2 size={16} />
                </button>
              </div>
            </div>
          ))}
          <div className="mt-4 flex justify-end items-center space-x-4">
            <div className="text-xl font-bold">Total: ${getTotalOrder()}</div>
          </div>
        </div>
      )}
    </div>
);


// Componente principal de la aplicación
const OrderManagementSystem: React.FC = () => {
    const [products, setProducts] = useState<Product[]>([]);
    const [orderItems, setOrderItems] = useState<OrderItem[]>([]);
    const [orderId, setOrderId] = useState<number>(0);
    const [customerInfo, setCustomerInfo] = useState<CustomerInfo>({
      companyName: '',
      contact: '',
      phone: '',
      email: '',
      direction: '',
      deliveryDate: '',
    });

    const [selectedProduct, setSelectedProduct] = useState<Product | null>(null);
    const [selectedColor, setSelectedColor] = useState<string>('#FFFFFF');
    const [selectedSize, setSelectedSize] = useState<string>('S');
    const [quantity, setQuantity] = useState<number>(1);
    const [logoFile, setLogoFile] = useState<string | null>(null);
    const [logoPosition, setLogoPosition] = useState<{ x: number; y: number }>({ x: 50, y: 50 });

    const productPreviewRef = useRef<HTMLDivElement>(null);
    const fileInputRef = useRef<HTMLInputElement>(null);
    const orderSummaryRef = useRef<HTMLDivElement>(null);

    // Cargar productos desde CSV
    useEffect(() => {
        Papa.parse(productsCsv, {
            header: true,
            dynamicTyping: true,
            complete: (result: ParseResult<Product>) => {
                setProducts(result.data);
            },
        });
    }, []);

    const handleLogoUpload = (e: ChangeEvent<HTMLInputElement>) => {
        const file = e.target.files?.[0];
        if (file) {
            const reader = new FileReader();
            reader.onloadend = () => {
                setLogoFile(reader.result as string);
            };
            reader.readAsDataURL(file);
        }
    };

    const handleLogoPosition = (e: React.MouseEvent<HTMLDivElement>) => {
        const rect = e.currentTarget.getBoundingClientRect();
        const x = ((e.clientX - rect.left) / rect.width) * 100;
        const y = ((e.clientY - rect.top) / rect.height) * 100;
        setLogoPosition({ x, y });
    };

    const addOrderItem = () => {
        if (selectedProduct) {
            const newOrderItem: OrderItem = {
                id: Date.now(),
                product: selectedProduct,
                color: selectedColor,
                size: selectedSize,
                quantity: quantity,
                logo: logoFile,
                logoPosition: logoPosition,
                subtotal: selectedProduct.basePrice * quantity,
            };
            setOrderItems([...orderItems, newOrderItem]);
            // Reiniciar la vista previa
            setSelectedProduct(null);
            setLogoFile(null);
            setQuantity(1);
            setLogoPosition({ x: 50, y: 50 });
            if (fileInputRef.current) {
                fileInputRef.current.value = '';
            }
        }
    };

    const updateOrderItem = (id: number, field: keyof OrderItem, value: any) => {
        setOrderItems((prevItems) =>
            prevItems.map((item) =>
                item.id === id ? { ...item, [field]: value } : item
            )
        );
    };

    const removeOrderItem = (id: number) => {
        setOrderItems((prevItems) => prevItems.filter((item) => item.id !== id));
    };

    const getTotalOrder = useCallback(() => {
        return orderItems.reduce((total, item) => total + item.subtotal, 0);
    }, [orderItems]);

    const generateOrderId = () => Date.now();

    const saveOrder = async () => {
        const newOrderId = generateOrderId();
        setOrderId(newOrderId);

        // Captura la imagen de cada producto individualmente
        const imagesToUpload = [];
        for (const item of orderItems) {
            if (item.logo) {
                imagesToUpload.push({
                    itemId: item.id,
                    imageData: item.logo
                });
            }
        }

        // Captura la imagen del resumen de la orden
        let orderSummaryImage = null;
        if (orderSummaryRef.current) {
            const canvas = await html2canvas(orderSummaryRef.current);
            orderSummaryImage = canvas.toDataURL('image/png');
        }

        const orderData: Order = {
            id: newOrderId,
            customer: customerInfo,
            items: orderItems,
            total: getTotalOrder(),
            status: 'Pendiente',
            createdAt: new Date().toISOString(),
            deliveryDate: customerInfo.deliveryDate,
        };

        try {
            const response = await fetch('http://localhost:3001/save-order', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    order: orderData,
                    images: imagesToUpload,
                    orderSummaryImage: orderSummaryImage
                }),
            });

            if (response.ok) {
                alert('Orden guardada exitosamente!');
                // Opcional: limpiar el estado después de guardar
                setOrderItems([]);
                setCustomerInfo({
                    companyName: '',
                    contact: '',
                    phone: '',
                    email: '',
                    direction: '',
                    deliveryDate: '',
                });
            } else {
                alert('Error al guardar la orden.');
            }
        } catch (error) {
            console.error('Error:', error);
            alert('Error de conexión con el servidor.');
        }
    };


    return (
        <div className="min-h-screen bg-gray-100 p-8">
            <div className="max-w-7xl mx-auto bg-white p-8 rounded-xl shadow-lg">
                <h1 className="text-3xl font-bold text-center mb-8 text-gray-800">
                    <FileText className="inline-block mr-3" size={32} />
                    Sistema de Gestión de Órdenes
                </h1>

                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
                    {/* Customer Info Section */}
                    <div className="col-span-1 md:col-span-2 lg:col-span-3">
                        <div className="bg-gray-50 p-6 rounded-lg shadow-inner">
                            <h2 className="text-xl font-bold mb-4 flex items-center">
                                <User className="mr-2" /> Información del Cliente
                            </h2>
                            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                                <input
                                    type="text"
                                    placeholder="Nombre de la Empresa"
                                    value={customerInfo.companyName}
                                    onChange={(e) => setCustomerInfo({ ...customerInfo, companyName: e.target.value })}
                                    className="p-3 border rounded-lg"
                                />
                                <input
                                    type="text"
                                    placeholder="Contacto"
                                    value={customerInfo.contact}
                                    onChange={(e) => setCustomerInfo({ ...customerInfo, contact: e.target.value })}
                                    className="p-3 border rounded-lg"
                                />
                                <input
                                    type="text"
                                    placeholder="Teléfono"
                                    value={customerInfo.phone}
                                    onChange={(e) => setCustomerInfo({ ...customerInfo, phone: e.target.value })}
                                    className="p-3 border rounded-lg"
                                />
                                <input
                                    type="email"
                                    placeholder="Email"
                                    value={customerInfo.email}
                                    onChange={(e) => setCustomerInfo({ ...customerInfo, email: e.target.value })}
                                    className="p-3 border rounded-lg"
                                />
                                <input
                                    type="text"
                                    placeholder="Dirección"
                                    value={customerInfo.direction}
                                    onChange={(e) => setCustomerInfo({ ...customerInfo, direction: e.target.value })}
                                    className="p-3 border rounded-lg"
                                />
                                <input
                                    type="date"
                                    placeholder="Fecha de Entrega"
                                    value={customerInfo.deliveryDate}
                                    onChange={(e) => setCustomerInfo({ ...customerInfo, deliveryDate: e.target.value })}
                                    className="p-3 border rounded-lg"
                                />
                            </div>
                        </div>
                    </div>

                    {/* Product and Customization Section */}
                    <div className="col-span-1 lg:col-span-2">
                        <div className="bg-gray-50 p-6 rounded-lg shadow-inner">
                            <h2 className="text-xl font-bold mb-4 flex items-center">
                                <Package className="mr-2" /> Productos y Personalización
                            </h2>
                            <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4 mb-6">
                                {products.map((product) => (
                                    <div
                                        key={product.id}
                                        onClick={() => setSelectedProduct(product)}
                                        className={`p-4 rounded-lg cursor-pointer transition transform hover:scale-105 ${selectedProduct?.id === product.id ? 'ring-2 ring-blue-500' : ''
                                            }`}
                                    >
                                        <img src={`./src/components/db/imgs/${product.image}`} alt={product.name} className="w-full h-auto rounded-lg mb-2" />
                                        <div className="text-center font-semibold text-sm">{product.name}</div>
                                        <div className="text-center text-xs text-gray-600">${product.basePrice}</div>
                                    </div>
                                ))}
                            </div>

                            {selectedProduct && (
                                <div className="mt-4 p-4 border rounded-lg flex flex-col md:flex-row items-center justify-between">
                                    <div className="w-full md:w-1/2">
                                        <h3 className="text-lg font-semibold mb-2">Personalizar {selectedProduct.name}</h3>
                                        <div className="flex items-center space-x-4 mb-4">
                                            <label htmlFor="quantity" className="font-semibold">Cantidad:</label>
                                            <input
                                                id="quantity"
                                                type="number"
                                                min="1"
                                                value={quantity}
                                                onChange={(e) => setQuantity(Number(e.target.value) || 1)}
                                                className="w-20 p-2 border rounded-lg text-center"
                                            />
                                        </div>

                                        <div className="flex items-center space-x-4 mb-4">
                                            <label htmlFor="color" className="font-semibold">Color:</label>
                                            <input
                                                id="color"
                                                type="color"
                                                value={selectedColor}
                                                onChange={(e) => setSelectedColor(e.target.value)}
                                                className="w-12 h-12 rounded-full cursor-pointer"
                                            />
                                        </div>

                                        <div className="flex items-center space-x-4 mb-4">
                                            <label htmlFor="size" className="font-semibold">Talla:</label>
                                            <select
                                                id="size"
                                                value={selectedSize}
                                                onChange={(e) => setSelectedSize(e.target.value)}
                                                className="p-2 border rounded-lg"
                                            >
                                                <option value="S">S</option>
                                                <option value="M">M</option>
                                                <option value="L">L</option>
                                                <option value="XL">XL</option>
                                            </select>
                                        </div>

                                        <div className="mb-4">
                                            <label className="font-semibold block mb-2">Subir Logo:</label>
                                            <input
                                                ref={fileInputRef}
                                                type="file"
                                                accept="image/*"
                                                onChange={handleLogoUpload}
                                                className="block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"
                                            />
                                        </div>

                                        <button
                                            onClick={addOrderItem}
                                            className="bg-green-500 hover:bg-green-600 text-white font-bold py-2 px-4 rounded-lg flex items-center transition duration-200"
                                        >
                                            <Plus className="mr-2" /> Agregar a la Orden
                                        </button>
                                    </div>
                                    <div className="w-full md:w-1/2 mt-6 md:mt-0 md:pl-6">
                                        <ProductPreview
                                            product={selectedProduct}
                                            color={selectedColor}
                                            logo={logoFile}
                                            logoPos={logoPosition}
                                            handleLogoPosition={handleLogoPosition}
                                            imageUrl={`./src/components/db/imgs/${selectedProduct.image}`}
                                        />
                                        {logoFile && (
                                            <div className="mt-4 text-center">
                                                <p className="text-sm text-gray-600">
                                                    Haz clic en la prenda para posicionar el logo
                                                </p>
                                            </div>
                                        )}
                                    </div>
                                </div>
                            )}
                        </div>
                    </div>

                    {/* Order Summary Section */}
                    <div className="col-span-1 md:col-span-2 lg:col-span-3">
                        <div ref={orderSummaryRef}>
                            <OrderSummary
                                orderItems={orderItems}
                                updateOrderItem={updateOrderItem}
                                removeOrderItem={removeOrderItem}
                                getTotalOrder={getTotalOrder}
                            />
                        </div>
                    </div>
                </div>

                <div className="mt-8 flex justify-end">
                    <button
                        onClick={saveOrder}
                        className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-3 px-6 rounded-lg flex items-center transition duration-200 text-lg"
                    >
                        <Save className="mr-2" />
                        Guardar Orden
                    </button>
                </div>
            </div>
        </div>
    );
};

export default OrderManagementSystem;
