import React, { useState, useRef, useCallback, ChangeEvent, useEffect, Ref } from 'react';
import html2canvas from 'html2canvas';

import {
  ShoppingCart,
  Upload,
  Edit3,
  Save,
  Trash2,
  Eye,
  Plus,
  FileText,
  Calendar,
  User,
  Phone,
  Mail,
  Package,
  ChevronLeft,
  ChevronRight
} from 'lucide-react';

import Papa, { ParseResult } from 'papaparse';
import productsCsv from '../db/Products.csv';

interface Product {
  id: number;
  name: string;
  basePrice: number;
  image: string;
  type: string;
}

interface OrderItem {
  id: number;
  product: Product;
  color: string;
  size: string;
  quantity: number;
  logo: string | null;
  logoPosition: { x: number; y: number };
  subtotal: number;
}

interface CustomerInfo {
  companyName: string;
  contact: string;
  phone: string;
  email: string;
  deliveryDate: string;
}

interface Order {
  id: number;
  customer: CustomerInfo;
  items: OrderItem[];
  total: number;
  status: string;
  createdAt: string;
  deliveryDate: string;
}

interface OrderSummaryProps {
  orderItems: OrderItem[];
  updateOrderItem: (id: number, field: keyof OrderItem, value: any) => void;
  removeOrderItem: (id: number) => void;
  getTotalOrder: () => number;
}

interface ProductPreviewProps {
  product: Product | null;
  color: string;
  logo: string | null;
  logoPos: { x: number; y: number };
  handleLogoPosition?: (event: React.MouseEvent<HTMLDivElement>) => void;
}

// Componente para la vista previa del producto (ahora con forwardRef)
const ProductPreview = React.forwardRef(({ product, color, logo, logoPos, handleLogoPosition }: ProductPreviewProps, ref: Ref<HTMLDivElement>) => {
    if (!product) {
      return (
        <div className="w-full h-80 rounded-lg bg-gray-100 flex items-center justify-center">
          <p className="text-gray-400">Selecciona un producto</p>
        </div>
      );
    }
    return (
      <div
        ref={ref}
        className="w-full h-80 rounded-lg flex items-center justify-center relative p-4 bg-gray-100"
      >
        <div
          className="relative w-full h-full"
          style={{ backgroundColor: color, borderRadius: '8px' }}
          onClick={handleLogoPosition}
        >
          <img
            src={product.image}
            alt={product.name}
            className="w-full h-full object-contain"
          />
          {logo && (
            <img
              src={logo}
              alt="Logo del cliente"
              style={{
                position: 'absolute',
                top: logoPos.y,
                left: logoPos.x,
                maxWidth: '30px',
                maxHeight: '30px',
                transform: 'translate(-50%, -50%)',
              }}
            />
          )}
        </div>
      </div>
    );
});

// Componente para el resumen de la orden
const OrderSummary = ({ orderItems, updateOrderItem, removeOrderItem, getTotalOrder }: OrderSummaryProps) => (
  <div className="bg-white rounded-lg shadow-lg p-6">
    <h3 className="text-xl font-bold mb-4 flex items-center">
      <FileText className="mr-2" />
      Resumen de la Orden
    </h3>
    {orderItems.length === 0 ? (
      <p className="text-gray-500 text-center py-8">
        No hay productos en la orden
      </p>
    ) : (
      <>
        {orderItems.map((item) => (
          <div key={item.id} className="border-b py-4 last:border-b-0">
            <div className="flex justify-between items-start">
              <div className="flex-1">
                <h4 className="font-semibold">{item.product.name}</h4>
                <p className="text-sm text-gray-600">
                  Color: <span
                    className="inline-block w-4 h-4 rounded-full ml-1"
                    style={{ backgroundColor: item.color }}
                  ></span>
                </p>
                <p className="text-sm text-gray-600">Tamaño: {item.size}</p>
                <p className="text-sm text-gray-600">Cantidad:
                  <input
                    type="number"
                    value={item.quantity}
                    onChange={(e) => updateOrderItem(item.id, 'quantity', parseInt(e.target.value))}
                    className="ml-2 w-16 px-2 py-1 border rounded"
                    min="1"
                  />
                </p>
                {item.logo && <p className="text-sm text-green-600">✓ Con logo personalizado</p>}
              </div>
              <div className="text-right">
                <p className="font-semibold">${item.subtotal.toLocaleString()}</p>
                <button
                  onClick={() => removeOrderItem(item.id)}
                  className="text-red-500 hover:text-red-700 mt-2"
                >
                  <Trash2 size={16} />
                </button>
              </div>
            </div>
          </div>
        ))}
        <div className="mt-4 pt-4 border-t">
          <div className="flex justify-between items-center text-xl font-bold">
            <span>Total:</span>
            <span>${getTotalOrder().toLocaleString()}</span>
          </div>
        </div>
      </>
    )}
  </div>
);

// Función para importar todas las imágenes del directorio imgs
const importAll = (r: __WebpackModuleApi.RequireContext) => {
  let images: { [key: string]: string } = {};
  r.keys().forEach((item: string) => {
    images[item.replace('./', '')] = r(item);
  });
  return images;
};

const images = importAll(require.context('./imgs', false, /\.(png|jpe?g|svg)$/));


const OrderManagementSystem = () => {
  const [orders, setOrders] = useState({
    pending: 12,
    inProcess: 8,
    completed: 45,
    todaySales: 8430
  });

  const [products, setProducts] = useState<Product[]>([]);
  const [selectedProduct, setSelectedProduct] = useState<Product | null>(null);
  const [selectedColor, setSelectedColor] = useState('#3B82F6');
  const [selectedSize, setSelectedSize] = useState('M');
  const [quantity, setQuantity] = useState(1);
  const [logoFile, setLogoFile] = useState<string | null>(null);
  const [logoPosition, setLogoPosition] = useState({ x: 50, y: 50 });
  const [orderItems, setOrderItems] = useState<OrderItem[]>([]);
  const [customerInfo, setCustomerInfo] = useState<CustomerInfo>({
    companyName: '',
    contact: '',
    phone: '',
    email: '',
    deliveryDate: ''
  });

  const productPreviewRef = useRef<HTMLDivElement>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);

  useEffect(() => {
    Papa.parse(productsCsv, {
      download: true,
      header: true,
      skipEmptyLines: true,
      complete: (result: ParseResult<Product>) => {
        setProducts(result.data.map(p => ({
          ...p,
          id: parseInt(String(p.id)),
          basePrice: parseFloat(String(p.basePrice))
        })));
      }
    });
  }, []);

  const handleProductSelect = (product: Product) => {
    setSelectedProduct(product);
  };

  const handleLogoUpload = (event: ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setLogoFile(reader.result as string);
        setLogoPosition({ x: 50, y: 50 }); // Resetear posición
      };
      reader.readAsDataURL(file);
    }
  };

  const handleLogoPosition = useCallback((event: React.MouseEvent<HTMLDivElement>) => {
    const element = event.currentTarget;
    const rect = element.getBoundingClientRect();
    const x = ((event.clientX - rect.left) / rect.width) * 100;
    const y = ((event.clientY - rect.top) / rect.height) * 100;
    setLogoPosition({ x, y });
  }, []);

  const handleCustomerInfoChange = (e: ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setCustomerInfo({ ...customerInfo, [name]: value });
  };

  const addProductToOrder = () => {
    if (selectedProduct) {
      const newOrderItem: OrderItem = {
        id: Date.now(),
        product: selectedProduct,
        color: selectedColor,
        size: selectedSize,
        quantity: quantity,
        logo: logoFile,
        logoPosition: logoPosition,
        subtotal: quantity * selectedProduct.basePrice,
      };
      setOrderItems([...orderItems, newOrderItem]);
      setQuantity(1);
      setLogoFile(null);
      setSelectedProduct(null);
    }
  };

  const updateOrderItem = (id: number, field: keyof OrderItem, value: any) => {
    setOrderItems(orderItems.map(item =>
      item.id === id ? { ...item, [field]: value } : item
    ));
  };

  const removeOrderItem = (id: number) => {
    setOrderItems(orderItems.filter(item => item.id !== id));
  };

  const getTotalOrder = () => {
    return orderItems.reduce((total, item) => total + item.subtotal, 0);
  };

  const saveOrder = async () => {
    if (orderItems.length === 0) {
      alert('Agrega al menos un producto a la orden');
      return;
    }

    if (!customerInfo.deliveryDate) {
      alert('Por favor ingresa la fecha de entrega');
      return;
    }

    // 1. Capturar la imagen de la vista previa
    let imageDataUrl = '';
    if (productPreviewRef.current) {
      const canvas = await html2canvas(productPreviewRef.current);
      imageDataUrl = canvas.toDataURL('image/png');
    }

    // 2. Crear el objeto de la orden
    const newOrder = {
      id: Date.now(),
      customer: customerInfo,
      items: orderItems,
      total: getTotalOrder(),
      status: 'Pendiente',
      createdAt: new Date().toLocaleDateString(),
      deliveryDate: customerInfo.deliveryDate
    };

    // 3. Enviar los datos al backend
    try {
      const response = await fetch('http://localhost:3001/save-order', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ 
          order: newOrder, 
          imageData: imageDataUrl 
        }),
      });

      const result = await response.json();

      if (response.ok) {
        alert(result.message);
      } else {
        alert(`Error: ${result.message}`);
      }

      // Resetear el formulario
      setOrderItems([]);
      setCustomerInfo({
        companyName: '',
        contact: '',
        phone: '',
        email: '',
        deliveryDate: ''
      });

    } catch (error) {
      console.error('Error al guardar la orden:', error);
      alert('No se pudo conectar con el servidor para guardar la orden.');
    }
  };

  return (
    <div className="bg-gray-100 min-h-screen p-8 font-sans">
      <div className="container mx-auto">
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-3xl font-bold text-gray-800">Sistema de Órdenes</h1>
          <div className="flex space-x-4 text-sm font-semibold text-gray-700">
            <div className="flex items-center space-x-2">
              <span className="text-red-500">{orders.pending}</span>
              <span>Pendientes</span>
            </div>
            <div className="flex items-center space-x-2">
              <span className="text-yellow-500">{orders.inProcess}</span>
              <span>En Proceso</span>
            </div>
            <div className="flex items-center space-x-2">
              <span className="text-green-500">{orders.completed}</span>
              <span>Completadas</span>
            </div>
          </div>
        </div>

        <div className="bg-white rounded-xl shadow-xl p-8">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">

            {/* Customer Information */}
            <div className="col-span-1 lg:col-span-1 bg-gray-50 rounded-lg p-6 shadow-sm">
              <h2 className="text-xl font-bold text-gray-800 mb-4 flex items-center">
                <User className="mr-2" />
                Información del Cliente
              </h2>
              <div className="space-y-4">
                <div>
                  <label htmlFor="companyName" className="text-sm font-medium text-gray-700 block mb-1">Empresa</label>
                  <div className="relative">
                    <User className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400" size={18} />
                    <input
                      type="text"
                      id="companyName"
                      name="companyName"
                      value={customerInfo.companyName}
                      onChange={handleCustomerInfoChange}
                      className="w-full pl-10 pr-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                      placeholder="Nombre de la empresa"
                    />
                  </div>
                </div>
                <div>
                  <label htmlFor="contact" className="text-sm font-medium text-gray-700 block mb-1">Contacto</label>
                  <div className="relative">
                    <User className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400" size={18} />
                    <input
                      type="text"
                      id="contact"
                      name="contact"
                      value={customerInfo.contact}
                      onChange={handleCustomerInfoChange}
                      className="w-full pl-10 pr-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                      placeholder="Nombre del contacto"
                    />
                  </div>
                </div>
                <div>
                  <label htmlFor="phone" className="text-sm font-medium text-gray-700 block mb-1">Teléfono</label>
                  <div className="relative">
                    <Phone className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400" size={18} />
                    <input
                      type="tel"
                      id="phone"
                      name="phone"
                      value={customerInfo.phone}
                      onChange={handleCustomerInfoChange}
                      className="w-full pl-10 pr-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                      placeholder="Número de teléfono"
                    />
                  </div>
                </div>
                <div>
                  <label htmlFor="email" className="text-sm font-medium text-gray-700 block mb-1">Email</label>
                  <div className="relative">
                    <Mail className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400" size={18} />
                    <input
                      type="email"
                      id="email"
                      name="email"
                      value={customerInfo.email}
                      onChange={handleCustomerInfoChange}
                      className="w-full pl-10 pr-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                      placeholder="Email de contacto"
                    />
                  </div>
                </div>
                <div>
                  <label htmlFor="deliveryDate" className="text-sm font-medium text-gray-700 block mb-1">Fecha de Entrega</label>
                  <div className="relative">
                    <Calendar className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400" size={18} />
                    <input
                      type="date"
                      id="deliveryDate"
                      name="deliveryDate"
                      value={customerInfo.deliveryDate}
                      onChange={handleCustomerInfoChange}
                      className="w-full pl-10 pr-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                    />
                  </div>
                </div>
              </div>
            </div>

            {/* Product Selection */}
            <div className="col-span-1 md:col-span-1 lg:col-span-2 bg-white rounded-lg p-6 shadow-lg">
              <h2 className="text-xl font-bold mb-4 flex items-center">
                <ShoppingCart className="mr-2" />
                Selección de Producto
              </h2>
              <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4 overflow-y-auto max-h-96 pr-4">
                {products.map(product => (
                  <div
                    key={product.id}
                    className={`cursor-pointer rounded-lg overflow-hidden border-2 p-2 transition-all duration-200 ${selectedProduct?.id === product.id ? 'border-blue-500 ring-2 ring-blue-500' : 'border-gray-200 hover:border-blue-300'}`}
                    onClick={() => handleProductSelect(product)}
                  >
                    <img
                      src={images[product.image]}
                      alt={product.name}
                      className="w-full h-24 object-contain mb-2"
                    />
                    <p className="text-sm font-semibold text-center">{product.name}</p>
                    <p className="text-xs text-gray-500 text-center">${product.basePrice.toLocaleString()}</p>
                  </div>
                ))}
              </div>
            </div>

            {/* Product Details & Preview */}
            <div className="col-span-1 md:col-span-2 lg:col-span-3 grid grid-cols-1 md:grid-cols-2 gap-8 mt-6">

              <div className="bg-white rounded-lg p-6 shadow-lg">
                <h3 className="text-xl font-bold mb-4 flex items-center">
                  <Edit3 className="mr-2" />
                  Detalles del Producto
                </h3>

                {selectedProduct && (
                  <div className="space-y-4">
                    <div className="flex items-center space-x-2">
                      <Package size={20} />
                      <p className="text-sm text-gray-700">Producto Seleccionado: <span className="font-semibold">{selectedProduct.name}</span></p>
                    </div>

                    <div>
                      <label htmlFor="color" className="text-sm font-medium text-gray-700 block mb-1">Color</label>
                      <input
                        type="color"
                        id="color"
                        value={selectedColor}
                        onChange={(e) => setSelectedColor(e.target.value)}
                        className="w-full h-10 rounded-lg border-2 border-gray-300"
                      />
                    </div>

                    <div>
                      <label htmlFor="size" className="text-sm font-medium text-gray-700 block mb-1">Talla</label>
                      <select
                        id="size"
                        value={selectedSize}
                        onChange={(e) => setSelectedSize(e.target.value)}
                        className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                      >
                        <option>XS</option>
                        <option>S</option>
                        <option>M</option>
                        <option>L</option>
                        <option>XL</option>
                      </select>
                    </div>

                    <div>
                      <label htmlFor="quantity" className="text-sm font-medium text-gray-700 block mb-1">Cantidad</label>
                      <input
                        type="number"
                        id="quantity"
                        value={quantity}
                        onChange={(e) => setQuantity(parseInt(e.target.value))}
                        className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                        min="1"
                      />
                    </div>

                    <div className="space-y-2">
                      <label className="text-sm font-medium text-gray-700">Logo del Cliente</label>
                      <div className="mt-1 flex justify-center px-6 pt-5 pb-6 border-2 border-gray-300 border-dashed rounded-md">
                        <div className="space-y-1 text-center">
                          {logoFile ? (
                            <img src={logoFile} alt="Logo" className="mx-auto h-12 w-12 text-gray-400" />
                          ) : (
                            <Upload className="mx-auto h-12 w-12 text-gray-400" />
                          )}
                          <div className="flex text-sm text-gray-600">
                            <label className="relative cursor-pointer bg-white rounded-md font-medium text-blue-600 hover:text-blue-500">
                              <span>Subir un archivo</span>
                              <input
                                ref={fileInputRef}
                                type="file"
                                className="sr-only"
                                accept="image/*"
                                onChange={handleLogoUpload}
                              />
                            </label>
                          </div>
                          <p className="text-xs text-gray-500">PNG, JPG, GIF hasta 10MB</p>
                        </div>
                      </div>
                    </div>

                    <button
                      onClick={addProductToOrder}
                      className="w-full bg-green-500 hover:bg-green-600 text-white font-bold py-2 px-4 rounded-lg flex items-center justify-center transition duration-200"
                    >
                      <Plus className="mr-2" />
                      Agregar a la Orden
                    </button>
                  </div>
                )}
              </div>

              <div className="bg-white rounded-lg p-6 shadow-lg flex flex-col items-center">
                <h3 className="text-xl font-bold mb-4 flex items-center">
                  <Eye className="mr-2" />
                  Vista Previa
                </h3>
                <ProductPreview
                  ref={productPreviewRef}
                  product={selectedProduct}
                  color={selectedColor}
                  logo={logoFile}
                  logoPos={logoPosition}
                  handleLogoPosition={handleLogoPosition}
                />
                {selectedProduct && (
                  <div className="mt-4 text-center">
                    <p className="text-sm text-gray-600">
                      {logoFile
                        ? 'Haz clic en la prenda para posicionar el logo'
                        : 'Selecciona un producto para ver la vista previa'}
                    </p>
                  </div>
                )}
              </div>
            </div>

            {/* Order Summary */}
            <div className="col-span-1 md:col-span-2 lg:col-span-3">
              <OrderSummary
                orderItems={orderItems}
                updateOrderItem={updateOrderItem}
                removeOrderItem={removeOrderItem}
                getTotalOrder={getTotalOrder}
              />
            </div>
          </div>
          <div className="mt-8 flex justify-end">
            <button
              onClick={saveOrder}
              className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-3 px-6 rounded-lg flex items-center transition duration-200 text-lg"
            >
              <Save className="mr-2" />
              Guardar Orden
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default OrderManagementSystem;
